mapper.update:
update category
name = #{name}, sort = #{sort}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}

mapper.select:
select id,type,name,sort,status,create_time,update_time,create_user,update_user from category
and name like concat('%', #{name},'%') and type = #{type}
order by sort asc

        select * from category

    and create_time = #{createTime}
and update_time = #{updateTime}
and create_user = #{createUser}
and update_user = #{updateUser}
and id = #{id}
and name = #{name}
and sort = #{sort}
and status = #{status}
and type = #{type}
